create table CHAPTER (INDEX INTEGER not null primary key,TOTAL_VERSE SMALLINT not null,START_VERSE SMALLINT not null,CHAPTER_NAME VARCHAR(250) not null,        REVELATION_ORDER SMALLINT not null,TYPE VARCHAR(6) not null CONSTRAINT CHAPTER_TYPE_CONSTRAINT CHECK (TYPE IN ('MAKKI','MADANI')),RUKUS SMALLINT not null);
create table LANGUAGE(ID INTEGER not null primary key AUTO_INCREMENT,ISO2NAME CHAR(8),LOCALE CHAR(6),IS_TRANSLITERATION CHAR(1) NOT NULL DEFAULT 'N' CONSTRAINT LANG_TRANSLIT_CONSTRAINT CHECK (IS_TRANSLITERATION  IN('Y','N')),TEXT_DIR CHAR(3) NOT NULL CONSTRAINT LANG_DIR_CONSTRAINT CHECK (TEXT_DIR IN('LTR','RTL')));
create table CHAPTERNAMES(ID INTEGER not null primary key AUTO_INCREMENT,CHAPTER_NAME VARCHAR(250) not null,CHAPTER_ID INTEGER CONSTRAINT CHAPTERNAMES_FK REFERENCES CHAPTER,LANG_ID INTEGER CONSTRAINT CHAPTERNAMES_LANG_ID REFERENCES LANGUAGE);
create table TRANSLATOR(ID INTEGER not null primary key AUTO_INCREMENT,NAME VARCHAR(100) NOT NULL,LOCALIZED_NAME VARCHAR(100),        LANG_ID INTEGER CONSTRAINT AUTHORS_LANG_ID REFERENCES LANGUAGE,        INTRODUCTION VARCHAR(10000));
create table VERSE(INDEX INTEGER not null primary key,VERSE_TEXT TEXT not null);
create table VERSE_I18N(ID INTEGER not null primary key AUTO_INCREMENT,VERSE_TEXT TEXT not null,VERSE_ID INTEGER CONSTRAINT VERSE_I18N_FK REFERENCES VERSE,LANG_ID INTEGER CONSTRAINT VERSE_I18N_LANG_ID REFERENCES LANGUAGE,TRANSLATOR_ID INTEGER CONSTRAINT VERSE_I18N_AUTHOR_ID REFERENCES TRANSLATOR);
#create table VERSE(INDEX INTEGER not null primary key ,VERSE_TEXT TEXT not null,LANG_ID INTEGER CONSTRAINT VERSE_I18N_LANG_ID REFERENCES LANGUAGE,TRANSLATOR_ID INTEGER CONSTRAINT VERSE_I18N_AUTHOR_ID REFERENCES TRANSLATOR);
